module.exports = {
  formatHours: function(hours) {
    if (hours > 12){
      formatted_hours = hours - 12
      am_pm = ' pm'
    } else {
      formatted_hours = hours
      am_pm = ' am'
    }
    return [formatted_hours, am_pm]
  },

  formatSec: function(sec) {
    if (sec < 10){
      formatted_sec = '0' + sec
    } else {
      formatted_sec = sec
    }
    return formatted_sec
  },

  formatMin: function(min) {
    if (min < 10){
      formatted_min = '0' + min
    } else {
      formatted_min = min
    }
    return formatted_min
  }
}
